CREATE TABLE PROJUDI.MOTIVO_BAIXA
(	
   ID_MOTIVO_BAIXA          NUMBER(10,0)   NOT NULL, 
   MOTIVO_BAIXA             VARCHAR2(255)  NOT NULL,
   CODIGO_TEMP              NUMBER(10,0)
);

CREATE UNIQUE INDEX PROJUDI.MOTIVO_BAIXA_IDX1 ON PROJUDI.MOTIVO_BAIXA (ID_MOTIVO_BAIXA);

ALTER TABLE PROJUDI.MOTIVO_BAIXA ADD CONSTRAINT PK_MOTIVO_BAIXA PRIMARY KEY(ID_MOTIVO_BAIXA);

CREATE SEQUENCE  PROJUDI.MOTIVO_BAIXA_ID_SEQ  MINVALUE 1 MAXVALUE 9999999999999999999999 INCREMENT BY 1 START WITH 60 NOCACHE  NOORDER  NOCYCLE;

CREATE OR REPLACE TRIGGER PROJUDI.MOTIVO_BAIXA_ID_TRG BEFORE INSERT OR UPDATE ON MOTIVO_BAIXA
FOR EACH ROW
DECLARE
v_newVal NUMBER(12) := 0;
v_incval NUMBER(12) := 0;
BEGIN
  IF INSERTING AND :new.ID_MOTIVO_BAIXA IS NULL THEN
    SELECT MOTIVO_BAIXA_ID_SEQ.NEXTVAL INTO v_newVal FROM DUAL;
   
   :new.ID_MOTIVO_BAIXA := v_newVal;
  END IF;
END;

ALTER TRIGGER PROJUDI.MOTIVO_BAIXA_ID_TRG ENABLE;

CREATE OR REPLACE FORCE VIEW PROJUDI.VIEW_MOTIVO_BAIXA AS 
  SELECT
    MB.ID_MOTIVO_BAIXA AS ID_MOTIVO_BAIXA,
    MB.MOTIVO_BAIXA AS MOTIVO_BAIXA,    
	MB.CODIGO_TEMP AS CODIGO_TEMP
  FROM 
    PROJUDI.MOTIVO_BAIXA MB;
	
